  `picstyle` int(1) NOT NULL DEFAULT '0' COMMENT '帖子列表是否以图片形式显示 0:否 1:是',
  `widthauto` int(1) NOT NULL DEFAULT '0' COMMENT '默认是否宽屏 0:全局 -1:宽屏 1:窄屏',
  `noantitheft` int(1) NOT NULL DEFAULT '0' COMMENT '禁止防采集',
  `noforumhidewater` int(1) NOT NULL DEFAULT '0' COMMENT '禁止隐藏水帖',
  `noforumrecommend` int(1) NOT NULL DEFAULT '0' COMMENT '禁止推荐回复',
  `livetid` int(8) NOT NULL DEFAULT '0' COMMENT '版块直播帖tid',
  `price` int(8) NOT NULL DEFAULT '0' COMMENT '进入版块所需支付的积分',
  PRIMARY KEY (`fid`),
  KEY `membernum` (`membernum`),
  KEY `dateline` (`dateline`),
  KEY `lastupdate` (`lastupdate`),
  KEY `activity` (`activity`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

