/*Table structure for table `xsmart_bbs_post` */

DROP TABLE IF EXISTS `xsmart_bbs_post`;

CREATE TABLE `xsmart_bbs_post` (
  `pid` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '帖子id',
  `tid` int(8) unsigned DEFAULT '0' COMMENT '主题id',
  `fid` int(8) unsigned DEFAULT '0' COMMENT '论坛id',
  `first` int(1) DEFAULT '0' COMMENT '是否是首贴',
  `author` varchar(15) DEFAULT '' COMMENT '作者姓名',
  `authorid` int(8) unsigned DEFAULT '0' COMMENT '作者id',
  `subject` varchar(80) DEFAULT '' COMMENT '标题',
  `content` text,
  `dateline` int(10) unsigned DEFAULT '0' COMMENT '发表时间',
  `message` text COMMENT '消息',
  `useip` varchar(15) DEFAULT '' COMMENT '发帖者IP',
  `invisible` int(1) DEFAULT '0' COMMENT '是否通过审核',
  `anonymous` int(1) DEFAULT '0' COMMENT '是否匿名',
  `usesig` int(1) DEFAULT '0' COMMENT '是否启用签名',
  `htmlon` int(1) DEFAULT '0' COMMENT '是否允许HTML',
  `bbcodeoff` int(1) DEFAULT '0' COMMENT '是否关闭BBCODE',
  `smileyoff` int(1) DEFAULT '0' COMMENT '是否关闭表情',
  `parseurloff` int(1) DEFAULT '0' COMMENT '是否允许粘贴URL',
  `attachment` int(1) DEFAULT '0' COMMENT '附件',
  `rate` int(6) DEFAULT '0' COMMENT '评分分数',
  `ratetimes` int(3) unsigned DEFAULT '0' COMMENT '评分次数',
  `status` int(10) DEFAULT '0' COMMENT '帖子状态',
  `tags` varchar(255) DEFAULT '0' COMMENT '新增字段，用于存放tag',
  `comment` int(1) DEFAULT '0' COMMENT '是否存在点评',
  `replycredit` int(10) DEFAULT '0' COMMENT '回帖获得积分记录',
  `position` int(8) unsigned NOT NULL DEFAULT '0' COMMENT '帖子位置信息',
  `looknum` int(255) NOT NULL,
  `recoverytime` int(11) unsigned NOT NULL,
  `alltip` int(255) NOT NULL,
  `is_show` int(1) NOT NULL,
  `lmorder` int(10) NOT NULL,
  `showindex` int(1) NOT NULL,
  `descr` varchar(255) NOT NULL,
  `sketch` varchar(255) DEFAULT NULL,
  `imgurl` varchar(255) DEFAULT NULL,
  `classid` int(11) DEFAULT NULL,
  `is_showindex` int(1) DEFAULT NULL,
  `sizecolor` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`pid`,`position`),
  UNIQUE KEY `pid` (`pid`),
  KEY `fid` (`fid`),
  KEY `authorid` (`authorid`,`invisible`),
  KEY `dateline` (`dateline`),
  KEY `invisible` (`invisible`),
  KEY `displayorder` (`tid`,`invisible`,`dateline`),
  KEY `first` (`tid`,`first`)
) ENGINE=MyISAM AUTO_INCREMENT=62 DEFAULT CHARSET=utf8;

