/*Table structure for table `xsmart_bbs_thread` */

DROP TABLE IF EXISTS `xsmart_bbs_thread`;

CREATE TABLE `xsmart_bbs_thread` (
  `tid` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '主题id',
  `fid` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '上级论坛',
  `posttableid` int(6) unsigned NOT NULL DEFAULT '0' COMMENT '帖子表ID',
  `typeid` int(6) unsigned NOT NULL DEFAULT '0' COMMENT '主题分类id',
  `sortid` int(6) unsigned NOT NULL DEFAULT '0' COMMENT '分类信息id',
  `readperm` int(3) unsigned NOT NULL DEFAULT '0' COMMENT '阅读权限',
  `price` int(6) NOT NULL DEFAULT '0' COMMENT '价格',
  `author` char(15) NOT NULL DEFAULT '' COMMENT '会员名',
  `authorid` int(8) unsigned NOT NULL DEFAULT '0' COMMENT '会员id',
  `subject` char(80) NOT NULL DEFAULT '' COMMENT '标题',
  `dateline` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '发表时间',
  `lastpost` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '最后发表',
  `lastposterid` int(11) NOT NULL COMMENT '最后发表人id',
  `lastposter` varchar(30) NOT NULL DEFAULT '' COMMENT '最后发表人',
  `views` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '浏览次数',
  `replies` int(8) unsigned NOT NULL DEFAULT '0' COMMENT '回复次数',
  `displayorder` int(1) NOT NULL DEFAULT '0' COMMENT '显示顺序',
  `highlight` int(1) NOT NULL DEFAULT '0' COMMENT '是否高亮',
  `digest` int(1) NOT NULL DEFAULT '0' COMMENT '是否精华',
  `rate` int(1) NOT NULL DEFAULT '0' COMMENT '是否评分',
  `special` int(1) NOT NULL DEFAULT '0' COMMENT '特殊主题,1:投票;2:商品;3:悬赏;4:活动;5:辩论贴;127:插件相关',
  `attachment` int(1) NOT NULL DEFAULT '0' COMMENT '附件,0无附件 1普通附件 2有图片附件',
  `moderated` int(1) NOT NULL DEFAULT '0' COMMENT '是否被管理员改动',
  `closed` int(8) unsigned NOT NULL DEFAULT '0' COMMENT '是否关闭',
  `stickreply` int(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否有回帖置顶',
  `recommends` int(6) NOT NULL DEFAULT '0' COMMENT '推荐指数',
  `recommend_add` int(6) NOT NULL DEFAULT '0' COMMENT '支持人数',
  `recommend_sub` int(6) NOT NULL DEFAULT '0' COMMENT '反对人数',
  `heats` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '主题热度值',
  `status` int(6) unsigned NOT NULL DEFAULT '0',
  `isgroup` int(1) NOT NULL DEFAULT '0' COMMENT '是否为群组帖子',
  `favtimes` int(8) NOT NULL DEFAULT '0' COMMENT '主题收藏次数',
  `sharetimes` int(8) NOT NULL DEFAULT '0' COMMENT '主题分享次数',
  `stamp` int(3) NOT NULL DEFAULT '-1' COMMENT '主题图章',
  `icon` int(3) NOT NULL DEFAULT '-1' COMMENT '主题图标',
  `pushedaid` int(8) NOT NULL DEFAULT '0' COMMENT '被推送到的文章aid',
  `cover` int(6) NOT NULL DEFAULT '0' COMMENT '主题封面 负数:远程　正数:本地 0:无封面',
  `replycredit` int(6) NOT NULL DEFAULT '0' COMMENT '回帖奖励积分主题记录积分值',
  `relatebytag` varchar(255) NOT NULL DEFAULT '0' COMMENT '根据帖子标签取的相关主题id (time /t tid,...)',
  `maxposition` int(8) unsigned NOT NULL DEFAULT '0' COMMENT '最大回帖位置信息',
  `bgcolor` char(8) NOT NULL DEFAULT '' COMMENT '保存高亮背景色',
  `comments` int(8) NOT NULL DEFAULT '0' COMMENT '点评数',
  PRIMARY KEY (`tid`),
  KEY `digest` (`digest`),
  KEY `sortid` (`sortid`),
  KEY `displayorder` (`fid`,`displayorder`,`lastpost`),
  KEY `typeid` (`fid`,`typeid`,`displayorder`,`lastpost`),
  KEY `recommends` (`recommends`),
  KEY `heats` (`heats`),
  KEY `authorid` (`authorid`),
  KEY `isgroup` (`isgroup`,`lastpost`),
  KEY `special` (`special`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

