/*Table structure for table `xsmart_bbs_thread_class` */

DROP TABLE IF EXISTS `xsmart_bbs_thread_class`;

CREATE TABLE `xsmart_bbs_thread_class` (
  `typeid` int(8) unsigned NOT NULL AUTO_INCREMENT,
  `fid` int(8) unsigned NOT NULL COMMENT '所属版块ID',
  `name` varchar(255) NOT NULL COMMENT '分类名称',
  `displayorder` int(9) NOT NULL COMMENT '显示顺序',
  `icon` varchar(255) NOT NULL COMMENT '分类图标URL',
  `moderators` int(1) NOT NULL DEFAULT '0' COMMENT '仅管理者可用',
  PRIMARY KEY (`typeid`),
  KEY `fid` (`fid`,`displayorder`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

