/*Table structure for table `xsmart_user_notification` */

DROP TABLE IF EXISTS `xsmart_user_notification`;

CREATE TABLE `xsmart_user_notification` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `uid` int(11) unsigned NOT NULL DEFAULT '0',
  `type` varchar(20) NOT NULL DEFAULT '' COMMENT '通知类型:"doing"记录,"friend"好友请求,"sharenotice"好友分享,"post"话题回复, ',
  `new` int(1) NOT NULL DEFAULT '0' COMMENT '通知是否为新:"1"为新通知,"0"为通知已读',
  `authorid` int(8) unsigned NOT NULL DEFAULT '0' COMMENT '作者用户ID',
  `author` varchar(15) NOT NULL DEFAULT '' COMMENT '用户名',
