  `dateline` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '通知产生的时间戳',
  `from_id` int(8) unsigned NOT NULL DEFAULT '0' COMMENT '来源对象id',
  `from_idtype` varchar(20) NOT NULL DEFAULT '' COMMENT '来源对象类型',
  `from_num` int(8) unsigned NOT NULL DEFAULT '0' COMMENT '来源量',
  `category` int(1) NOT NULL DEFAULT '0' COMMENT '提醒分类 1我的帖子 2坛友互动 3系统消息 4管理工作 0应用消息',
  PRIMARY KEY (`id`),
  KEY `uid` (`uid`,`new`,`dateline`),
